<script>$(function(){$("#respond").addClass("none"),$(".resTog").click(function(){$("#respond").toggleClass("none")}),$(".comments-link").click(function(){$("#respond").removeClass("none")})});</script><?php if (post_password_required()){return;}$discussion=twentynineteen_get_discussion_data();?> <div id="comments" class="<?php echo comments_open()?'comments-area':'comments-area comments-closed'; ?>"> <div class="<?php echo $discussion->responses>0?'comments-title-wrap':'comments-title-wrap no-responses';?>"> <h2 class="comments-title"> <?php if(comments_open()){if( have_comments()) {_e('Join the Conversation','twentynineteen');}else {echo "<a class='resTog'>留下评论</a>";}}else{if('1'==$discussion->responses){printf(_x('One reply on &ldquo;%s&rdquo;','comments title','twentynineteen'),get_the_title());}else{printf(_nx('%1$s reply on &ldquo;%2$s&rdquo;','%1$s replies on &ldquo;%2$s&rdquo;',$discussion->responses,'comments title','twentynineteen'),number_format_i18n($discussion->responses),get_the_title());}}?> </h2> <?php if(have_comments()&&comments_open()){get_template_part('template-parts/post/discussion','meta');}?> </div> <?php if(have_comments()):if(comments_open()){twentynineteen_comment_form('desc');}?> <ol class="comment-list"> <?php wp_list_comments(array('walker'=> new TwentyNineteen_Walker_Comment(),'avatar_size' => twentynineteen_get_avatar_size(),'short_ping'=> true,'style'=>'ol',));?> </ol> <?php if(have_comments()):$prev_icon=twentynineteen_get_icon_svg('chevron_left',22);$next_icon= twentynineteen_get_icon_svg('chevron_right',22);$comments_text=__('Comments','twentynineteen');the_comments_navigation(array('prev_text'=>sprintf('%s <span class="nav-prev-text"><span class="primary-text">%s</span><span class="secondary-text">%s</span></span>',$prev_icon,__('更早的'), __('评论')),'next_text'=>sprintf('<span class="nav-next-text"><span class="primary-text">%s</span><span class="secondary-text">%s</span></span> %s',__( '下一页'),__('评论'),$next_icon),));endif;if(comments_open()&&'asc'===strtolower(get_option('comment_order','asc'))):?> <div class="comment-form-flex"> <span class="screen-reader-text"><?php _e('Leave a comment','twentynineteen');?></span> <?php twentynineteen_comment_form('asc');?> <h2 class="comments-title"><a class='resTog'><?php _e('Leave a comment','twentynineteen');?></a></h2> </div> <?php endif;if(!comments_open()):?> <p class="no-comments"> <?php _e('Comments are closed.','twentynineteen');?> </p> <?php endif;else:twentynineteen_comment_form(true);endif;?> </div>